import prisma from "../../lib/prisma";

export default async function CreateSite(req, res) {
  const { name, subdomain, description, userId } = req.body;
  const response = await prisma.site.create({
    data: {
      name: name,
      description: description,
      subdomain: subdomain,
      logo: "/logo.png",
      image: `https://og-image.vercel.app/${encodeURIComponent(
        name
      )}.png?theme=dark&md=1&fontSize=100px&images=${encodeURIComponent(
        "https://assets.vercel.com/image/upload/v1641523844/platforms/logo.png"
      )}`,
      imageBlurhash:
        "data:image/png;base64,",
      user: {
        connect: {
          id: userId,
        },
      },
    },
  });
  res.status(200).json({ siteId: response.id });
}
